body.raw:
query EventsQuery {
  httpEvents(
    limit: 5, 
    filter: {
      tsRange: {begin:"2022-11-20T10:10:10", end:"2022-11-27T10:10:10"}
    },
    aggregate: {count: requestUri}
    groupBy: [requestUri]
    orderBy: [count_DESC]
    ) 
  { 
    requestUri
    count
  }
}

